#[component]
pub fn SidebarButton() -> impl IntoView {
    let sidebar = expect_context::<SidebarContext>();

    view! {
        <Tooltip side="bottom" align="start" value="Toggle sidebar">
            <Button variant="ghost" size="sm-icon">
                <Show
                    when=move || { sidebar.hidden.get() }
                    fallback=move || match sidebar.side.get().as_str() {
                        "right" => view! { {icon!(icondata::LuPanelRightClose)} }.into_any(),
                        _ => view! { {icon!(icondata::LuPanelLeftClose)} }.into_any(),
                    }
                >
                    {match sidebar.side.get().as_str() {
                        "right" => view! { {icon!(icondata::LuPanelRightOpen)} }.into_any(),
                        _ => view! { {icon!(icondata::LuPanelLeftOpen)} }.into_any(),
                    }}
                </Show>
            </Button>
        </Tooltip>
    }
}

#[component]
pub fn DebugMenu() -> impl IntoView {
    {
        if cfg!(debug_assertions) {
            view! {
                <SidebarGroup>
                    <SidebarGroupLabel>"Debug"</SidebarGroupLabel>
                    <SidebarGroupContent>
                        <SidebarMenu>
                            <SidebarMenuItem>
                                <SidebarMenuButton>
                                    <A href="/reactive-debug">
                                        {icon!(icondata::BsJoystick)} <span>"Reactivity"</span>
                                    </A>
                                </SidebarMenuButton>
                            </SidebarMenuItem>
                            <SidebarMenuItem>
                                <Collapsible>
                                    <CollapsibleTrigger slot>
                                        <SidebarMenuButton>
                                            {icon!(icondata::LuSettings)} "Test Menu"
                                        </SidebarMenuButton>
                                    </CollapsibleTrigger>
                                    <CollapsibleContent>
                                        <SidebarMenuSub>
                                            <SidebarMenuSubItem>
                                                <a href="#">
                                                    <span>"One"</span>
                                                </a>
                                            </SidebarMenuSubItem>
                                        </SidebarMenuSub>
                                        <SidebarMenuSub>
                                            <SidebarMenuSubItem>
                                                <a href="#">
                                                    <span>"Two"</span>
                                                </a>
                                            </SidebarMenuSubItem>
                                        </SidebarMenuSub>
                                        <SidebarMenuSub>
                                            <SidebarMenuSubItem>
                                                <a href="#">
                                                    <span>"Three"</span>
                                                </a>
                                            </SidebarMenuSubItem>
                                        </SidebarMenuSub>
                                        <SidebarMenuSub>
                                            <SidebarMenuSubItem>
                                                <a href="#">
                                                    <span>"Four"</span>
                                                </a>
                                            </SidebarMenuSubItem>
                                        </SidebarMenuSub>
                                    </CollapsibleContent>
                                </Collapsible>
                            </SidebarMenuItem>
                        </SidebarMenu>
                    </SidebarGroupContent>
                </SidebarGroup>
            }
            .into_any()
        } else {
            ().into_any()
        }
    }
}

